insert_data(database_makanan, "Tinutuan", "Olahan beras yang menjadi bubur dimasak dengan aneka umbi dan sayur", "appetizer", ["200 gram labu kuning, kukus", "100 gram labu kuning", "100 gram jagung manis", "150 gram beras", "2 liter air","1 sdm garam", "2 ikat daun kangkung", "2 ikat daun bayam", "3 tangkai daun kemangi", "100 gram ikan cakalang", "3 sdm sambal roa", "3 buah lemon"], "1. Kukus serta haluskan labu kuning 2. Potong kotak labu kuning 3. rebus jagung manis serta pipilkan 4. siangi 2 ikat daun kangkung serta daun bayam 5. Ambil daun kemangi dari tangkai nya 6. Potong-potong ikan cakalang 7. Dalam satu panci besar, rebus beras, air, labu kuning yang sudah dikukus, dan beri garam. Aduk sampai beras setengah matang 8. Masukkan jagung, ubi merah, labu kuning. Aduk dan masak sampai matang 9.Saat bubur sudah matang, masukkan kangkung, bayam, dan daun kemangi 10. Sajikan dengan sambal roa dan suwiran ikan cakalang", 15000)

insert_data(database_makanan, "Salad Buah Mayonaise", "Olahan Salah buah dengan saos mayonaise", "appetizer", ["1 Buah pir", "1 Buah Sunkist", "1 Buah jeruk medan", "0.5 Buah bengkoang", "150 gram Anggur merah", "100 gram Strawberry", "6 sdm Mayonaise", "4 sdm Susu Kental Manis", "Keju parut secukupnya"], "1. Cuci Bersih buah 2. Potong buah sesuai selera potongan 3. Campurkan semua buah ke dalam satu wadah 4. Beri mayonaise buah yang suah di campurkan 5. Berikan taburan keju parut dan aduk kembali sampai rata 6. Sajikan dengan porsi yang di inginkan", 17000)

insert_data(database_makanan, "Pancake Mangga", "Buah mangga yang di lapisi dengan kulit pancake", "dessert", ["125 gr tepung terigu", "1 butir telur ayam", "1/4 sdt garam", "275 ml susu cair", "Buah Mangga", "Whip Cream"], "1. kocok telur ayam terlebih dahulu 2. Campurkan tepung terigu, garam dan telur jadi satu 3.Tambahkan susu cair secara perlahan sambil diaduk-aduk 4. Saring adonan 5. Dadar di tempat penggoreng sampai adonan habis 6. Isi kulit pancake dengan whipping cream dan mangga, lipat seperti amplop 7. Simpan pancake di kulkas. Pancake mangga enak dimakan dalam kondisi dingin", 1900)

insert_data(database_makanan, "Taco Ikan", "Taco Ikan dengan selada dan kubis herbal", "diet food", ["Ikan Tuna","kulit lumpia","kubis","selada","Tarragon","jeruk nipis", "garam", "½ cangkir krim asam", "1 sdt jintan tanah1 jalapeno","2 sdm air jeruk nipis", "Perasan 1 buah jeruk nipis"], "1. Bersihkan Tuna dan Potong sesuai selera, 2. Campurkan bahan selada dan kubis serta beri garam 3. Tunggu 10 menit 4. Campurkan bahan untuk krim dan beri bumbu dengan garam 5. Goreng Ikan tuna dengan bumbu selada serta sudah di bungkus dengan kulit lumpia 6. Setelah itu sajikan di piring", 45000)

