insert_data(database_makanan, "Sup Sayur", "Sup dengan aneka sayur ", "appetizer", ["1 buah Wortel", "¼ bagian Kol", "¼ bagian Bunga kol", "1 batang Daun bawang", "1 batang Seledri", "1 buah Kentang", "2 butir Bawang putih", "2 butir Bawang merah", "1 sdt lada bubuk", "1 sdm Kaldu bubuk", "1 sdm Minyak goreng", "1 bungkus Makaroni", "Garam secukupnya", "Air secukupnya"], "1. Potong wortel, kol, bunga kol, daun bawang, seledri dan kentang. 2. Haluskan bumbu bawang putih, bawang merah, lada, dan garam. 3. Tumis bumbu hingga wangi, lalu tambahkan air. 4. Masukkan sayur-sayuran dan makaroni, tambahkan kaldu bubuk. 5. Rebus hingga masak, dan siap disajikan", 7000)
insert_data(database_makanan, "Onion Rings", "Bawang bombay bentuk ring goreng tepung", "appetizer", ["1 buah Bawang Bombay", "20 gram Tepung Bumbu Serbaguna", "20 gram Tepung panir", "Air secukupnya", "Minyak Goreng"], "1. Cuci bawang bombay lalu iris membentuk lingkaran. 2. Buat adonan basah dengan mencampur air dan tepung bumbu. 3. Masukkan irisan bawang bombay ke adonan basah lalu lumuri dengan tepung panir. 4. Goreng hingga kuning kecoklatan, tiriskan, lalu siap disajikan.", 13500)
insert_data(database_makanan, "Udang Alpukat", "Udang panggang diatas krim alpukat dan mentimun", "appetizer", ["250 gram Udang", "1 butir Bawang putih", "½ sdt ketumbar", " ½ sdt Cabai bubuk", "½ sdt Garam", "2 sdm Minyak zaitun", "1/4 sdt lada hitam", "1 buah Alpukat", "Jeruk nipis secukupnya", "½ buah Mentimun"], "1. Masukkan udang dalam mangkuk, dan tambahkan bawang putih cincang,ketumbar, cabai bubuk, garam, lada, dan minyak zaitun. 2. Tumis udang hingga matang. 3. Masukkan alpukat, garam, air jeruk nipis, campur hingga merata. 4. Potong tipis mentimun. 5. Tata irisan mentimun, letakkan krim alpukat diatasnya, dan letakkan tumisan udang diatas krim alpukat. 6. Makanan siap disajikan", 40000)
insert_data(database_makanan, "Soto Lamongan", "Soto khas Lamongan dengan kuah kuning dan taburan koya yang gurih", "main course", ["100 gram Daging Ayam", "1 siung Serai", "2 lembar Daun Jeruk", "2 lembar Daun Salam", "1 cm Lengkuas", "2 sdt Garam", "1 batang Daun Bawang", "Air secukupnya", "Bumbu halus (1 butir Bawang merah, 2 butir Bawang putih, 1 butir Kemiri, 2 cm Kunyit, ½ sdt Merica)", "Koya", "200 gram Soun", "1 buah Tomat", "2 lembar Kol", "1 butir Telur", "Seledri secukupnya", "Jeruk Nipis secukupnya"], "1. Haluskan bumbu halus. 2. Iris tomat, seledri, kol, dan daun bawang. 3. Rebus daging ayam hingga matang dan berkaldu. 4. Tumis bumbu halus hingga wangi lalu tambahkan garam, serai, lengkuas, daun jeruk, daun salam, dan kaldu ayam. 5. Goreng ayam hingga matang, lalu suwir-suwir ayam. 6. Tata soun, tomat, kol, telur rebus, dan suwiran ayam dalam mangkuk, lalu tambahkan kuah soto. 7. Taburkan koya, seledri dan daun bawang sesuai selera", 12000)
insert_data(database_makanan, "Steak Ayam", "Steak ayam dengan saus pedas manis", "main course", ["1 potong Dada ayam", "½ sdt Merica bubuk", "1 butir bawang putih parut", "1 sdt garam", "1 sdm Mentega", "2 sdm Minyak sayur", "1 buah Bawang bombay cincang halus", "3 buah Cabai rawit", "1 sdm Tepung terigu", "250 mL Kaldu ayam", "1 sdm Kecap manis", "1 sdm Saus tiram", "Kentang goreng", "Salad sayur"], "1. Cuci bersih daging dada ayam. Kerat-kerat permukaannya di beberapa tempat. 2. Lumuri daging ayam dengan bawang putih, merica dan garam hingga rata. Diamkan beberapa saat. 3. Panaskan mentega dalam wajan hingga leleh. Kecilkan api, masukkan daging dada ayam. 4. Masak hingga kedua sisinya kering dan matang, angkat dan sisihkan. 5. Tumis bawang bombay dan bawang putih hingga layu dan harum. 6. Taburkan tepung terigu lalu aduk hingga tercampur rata. 7. Tuangi kaldu sambil aduk hingga agak kental. 8. Tambahkan bahan-bahan lainnya dan masak hingga mendidih. 9. Masukkan daging dada ayam, masak sebentar hingga mendidih lalu angkat. 10. Sajikan dengan salad sayur dan kentang goreng.", 24000)
insert_data(database_makanan, "Bubur Candil", "Bubur dengan bulatan kenyal yang legit", "dessert", ["50 gram Tepung Ketan", "1 sdm Tepung beras", "2 sdm Tepung tapioka", "Air secukupnya", "50 gram Gula merah", "2 lembar Daun pandan", "Garam secukupnya", "75 mL Santan"], "1. Didihkan air, gula merah, garam, dan daun pandan. 2. Campur tepung ketan, tepung beras, air secukupnya, dan garam, kemudian buat bulatan kecil. 3. Masukkan bulatan candil kedalam rebusan pada langkah pertama. 4. Tuang tepung tapioka dan sedikit air. 5. Didihkan santan dengan daun pandan dan garam. 6. Sajikan bubur candil dengan siraman santan", 8000)
insert_data(database_makanan, "Pie Buah", "Pie dengan topping buah segar", "dessert", ["250 gram Tepung Terigu", "1 sdm Gula halus", "170 gram Mentega", "1 butir Kuning telur", "Susu cair", "1 sdt Gula pasir", "40 gram Tepung Meizena", "½ sdt Vanilla bubuk", "Air 100 mL", "Aneka buah"], "1. Campur beberapa bahan ini sekaligus yakni terigu, gula halus, mentega. Setelah itu aduk menggunakan sendok kayu atau pisau pastry sampai berbulir kemudian tambahkan kuning telur, dan aduk rata. 2. Bentuk adonan menjadi bulat-bulat, selanjutnya taruh cetakan pie dalam bentuk molding yang sudah dioles butter. Jangan lupa pakai garpu untuk menusuk bagian dasar pie supaya tidak menggelembung. 3. Sekarang giliran memanggang. Perhatikan suhu 165/170’c api atas bawah dan mulai panggang pie hingga matang. Akan memakan waktu sekitar 25 menit atau sesuaikan oven masing-masing. 4. Ambil tepung maizena kemudian cairkan dengan susu cair (seperlunya saja), campur 5 butir kuning telur, aduk rata. 5. Kini rebus susu cair sisa ditambah gula hingga mendidih. Masukkan campuran maizena dan kuning telur, aduk cepat, terus biarkan mendidih sekali lagi. Angkat. dan sisihkan. 6. Isi pie dengan fla, tambahan hiasan potongan buah, siram 1 sdt agar-agar bening. Dan siap sajikan.", 16500)
insert_data(database_makanan, "Roti Gandum Telur Alpukat", "Roti gandum panggang dengan telur dan alpukat", "diet food", ["2 lembar roti gandum", "1 buah Telur", "1 buah Alpukat", "2 sdm Mentega", "Garam secukupnya"], "1. Panggang roti gandum dengan mentega hingga matang lalu sisihkan. 2. Panggang telur dengan mentega dan beri sedikit garam hingga matang lalu sisihkan. 3. Haluskan buah alpukat. 4. Oleskan alpukat di atas kedua roti. 5. Letakkan telur disalah satu roti lalu tutup dengan roti satunya", 10000)
insert_data(database_makanan, "Smoothie Hijau Buah dan Sayur", "Smoothie hijau dengan campuran buah dan sayur untuk pelengkap menu diet", "diet food", ["1 buah Mentimun", "½ buah Nanas", "1 batang Seledri", "200 mL Air", "50 gram Yogurt"], "1. Potong timun, nanas, dan seledri. 2. Masukkan bahan ke dalam blender. 3. Blender hingga halus, kemudian smoothie hijau siap disajikan", 16000)
insert_data(database_makanan, "Granola", "Campuran gandum utuh, kacang-kacangan dan buah kering yang sehat", "diet food", ["1 cup Rolled oat", "2 sdm Madu", "1 sdm Minyak zaitun", "2 sdm Kacang-kacangan","2 sdm Buah kering (kurma, berries, kismis, dll.)", "Susu atau yogurt secukupnya", "Gula aren"], "1. Sangrai rolled oat dan kacang-kacangan hingga kecoklatan. 2. Masukkan bahan untuk saus, antara lain minyak zaitun,gula aren, madu, dan buah kering. 3. Campur dengan rata siap di sajikan dengan susu atau yogurt.", 30000)

